import React, { useState, useEffect } from 'react'
import {
  Text,
  Platform,
  Keyboard,
  SafeAreaView,
  StyleSheet,
  Alert,
  Button,
  StatusBar,
  View,
} from 'react-native'
import { GooglePlacesAutocomplete } from 'react-native-google-places-autocomplete'
import { useNavigation } from '@react-navigation/core'
import { images } from '../data/dummy-data'
import { Header } from 'react-native-elements'
import axios from '../axios'
import AsyncStorage from '@react-native-async-storage/async-storage'

const BillingDetailsScreen = () => {
  const [address, setAddress] = useState()
  const navigation = useNavigation()

  //prevent the user to left the screen until he insert all the data
  useEffect(() => {
    navigation.addListener('beforeRemove', (e) => {
      e.preventDefault()
      Alert.alert(
        'Dati necessari',
        'perfavore inserisci indirizzo di spedizione simile all esempio fornito',
      )
    })
  }, [navigation])

  const handleSubmit = async () => {
    const token = AsyncStorage.getItem('token')

    let config = {
      headers: {
        Authorization: 'Bearer ' + token,
      },
    }

    const data = axios.post(
      '/address',
      {
        indirizzo: address,
      },
      config,
    )

    navigation.navigate('AccountHome')
  }

  return (
    <SafeAreaView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      onPress={() => {
        Keyboard.dismiss()
      }}
      style={{ flex: 1 }}
    >
      <Header
        backgroundImage={images.otherImages.royaltyBackground}
        backgroundImageStyle={{ borderRadius: 30 }}
        statusBarProps={{ barStyle: 'light-content' }}
        leftContainerStyle={{
          marginLeft: 10,
        }}
        backgroundColor="#F6F7FA"
        containerStyle={{
          height: 150,
        }}
      />
      <View style={{ padding: 15, flex: 1 }}>
        <Text style={styles.heading}> Indirizzo di spedizione </Text>
        <Text style={styles.text}>
          inserisci il tuo indirizzo per intero, se Ã¨ possibile con il civico
        </Text>
        {!address ? (
          <Text style={styles.text}>
            Esempio: Via Cagliari, 11, Catania, CT, Italia
          </Text>
        ) : (
          <Text style={styles.text}>
            E' simile all'esempio mostrato? Potrai comunque cambiarlo per
            confermare l'indirizzo di spedizione
          </Text>
        )}

        <GooglePlacesAutocomplete
          placeholder="Search"
          minLength={2} // minimum length of text to search
          autoFocus={false}
          returnKeyType={'search'} // Can be left out for default return key https://facebook.github.io/react-native/docs/textinput.html#returnkeytype
          listViewDisplayed="auto" // true/false/undefined
          fetchDetails={true}
          renderDescription={(row) => row.description} // custom description render
          onPress={(data, details = null) => {
            setAddress(data.terms)
          }}
          query={{
            // available options: https://developers.google.com/places/web-service/autocomplete
            key: 'AIzaSyDCetXIWkMO_4b_NH-s_lzepRCym53wp1Q',
            language: 'it', // language of the results
            types: 'address', // default: 'geocode'
          }}
          styles={{
            description: {
              fontWeight: 'bold',
            },
            predefinedPlacesDescription: {
              color: '#1faadb',
            },
          }}
          nearbyPlacesAPI="GooglePlacesSearch" // Which API to use: GoogleReverseGeocoding or GooglePlacesSearch
          GooglePlacesDetailsQuery={{
            fields: 'formatted_address',
          }}
          filterReverseGeocodingByTypes={[
            'locality',
            'administrative_area_level_3',
          ]} // filter the reverse geocoding results by types - ['locality', 'administrative_area_level_3'] if you want to display only cities
          debounce={200}
        />

        <Button
          title="conferma"
          disabled={!address}
          onPress={() => handleSubmit()}
          color="#094067"
        />
      </View>
      <StatusBar style="light" />
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  heading: {
    fontFamily: 'Inter_600SemiBold',
    fontSize: 30,
    color: '#094067',
    marginBottom: 20,

    marginTop: 20,
  },
  text: {
    fontFamily: 'Inter_600SemiBold',
    fontSize: 15,
    color: '#094067',
    marginBottom: 20,
    marginLeft: 8,
    marginTop: 10,
  },
})

export default BillingDetailsScreen
