import React, {
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
  useRef,
} from 'react'
import { GiftedChat } from 'react-native-gifted-chat'
import { db } from '../firebase'

import Constants from 'expo-constants'
import * as Notifications from 'expo-notifications'
import { Header } from 'react-native-elements'
import { images } from '../data/dummy-data'
import { StatusBar } from 'expo-status-bar'

Notifications.setNotificationHandler({
  handleNotification: async () => ({
    shouldShowAlert: true,
    shouldPlaySound: false,
    shouldSetBadge: false,
  }),
})

const ChatScreen = () => {
  // Can use this function below, OR use Expo's Push Notification Tool-> https://expo.dev/notifications
  const sendPushNotification = async (expoPushToken) => {
    const token = await registerForPushNotificationsAsync()

    const message = {
      to: token,
      sound: 'default',
      title: 'Original Title',
      body: 'And here is the body!',
      data: { someData: 'goes here' },
    }

    await fetch('https://exp.host/--/api/v2/push/send', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Accept-encoding': 'gzip, deflate',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(message),
    })
  }

  // notification setup
  const [expoPushToken, setExpoPushToken] = useState('')
  const [notification, setNotification] = useState(false)
  const notificationListener = useRef()
  const responseListener = useRef()

  const [messages, setMessages] = useState([])

  const textRef = useRef(true)

  useEffect(() => {
    // This listener is fired whenever a notification is received while the app is foregrounded
    notificationListener.current = Notifications.addNotificationReceivedListener(
      (notification) => {
        console.log(notification, ' here')
        sendPushNotification()
      },
    )

    // This listener is fired whenever a user taps on or interacts with a notification (works when app is foregrounded, backgrounded, or killed)
    responseListener.current = Notifications.addNotificationResponseReceivedListener(
      (response) => {
        console.log(response, ' swag')
      },
    )

    // for real update
    const subscribe = db.collection('chats').onSnapshot(async (snapshot) => {
      snapshot.docChanges().forEach((change) => {
        console.log(change.type)
        if (change.type == 'added') {
          let data = change.doc.data()
          data.createdAt = data.createdAt

          setMessages((prevMessages) => GiftedChat.append(prevMessages, data))
        }
      })
      // send the notification to the user
    })

    // set the welcome message
    textRef.current &&
      setMessages((previousMessages) =>
        GiftedChat.append(previousMessages, {
          _id: 1,

          text:
            'Puoi chiedermi di tutti gli stati degli ordini, delle novità e informazioni!',
          createdAt: new Date(),
          user: {
            _id: 2,
            name: 'SiciliaNaturalmente',
            avatar:
              'https://www.accademiasicilianadellapasta.it/wp-content/uploads/2020/06/Logo-Sicilia.jpg',
          },
        }),
      )

    return () => {
      subscribe()
      Notifications.removeNotificationSubscription(notificationListener.current)
      Notifications.removeNotificationSubscription(responseListener.current)
      textRef.current = 'false'
    }
  }, [])

  useLayoutEffect(() => {
    // query the message

    setTimeout(() => {
      fetchMessage()
    }, 3000)

    return
  }, [])

  const fetchMessage = async () => {
    const coll = db.collection('chats')

    coll
      .where('user._id', 'in', ['alfri.lipari@gmail.com', 1]) // here put the email of the user
      .orderBy('createdAt', 'desc')
      .get()
      .then((query) =>
        setMessages(
          query.docs.map((doc) => ({
            _id: doc.data()._id,
            createdAt: doc.data().createdAt,
            text: doc.data().text,
            user: doc.data().user,
          })),
        ),
      )
  }

  const onSend = useCallback(async (messages = []) => {
    console.log(messages[0], 'ssh')
    const { _id, text, user } = messages[0]
    const createdAt = Math.round(new Date().getTime() / 1000)
    db.collection('chats').add({
      _id,
      createdAt,
      text,
      user,
    })
  }, [])

  async function registerForPushNotificationsAsync() {
    let token
    if (Constants.isDevice) {
      const {
        status: existingStatus,
      } = await Notifications.getPermissionsAsync()
      let finalStatus = existingStatus
      if (existingStatus !== 'granted') {
        const { status } = await Notifications.requestPermissionsAsync()
        finalStatus = status
      }
      if (finalStatus !== 'granted') {
        alert('Failed to get push token for push notification!')
        return
      }
      token = (await Notifications.getExpoPushTokenAsync()).data
      console.log(token)
    } else {
      alert('Must use physical device for Push Notifications')
    }

    if (Platform.OS === 'android') {
      Notifications.setNotificationChannelAsync('default', {
        name: 'default',
        importance: Notifications.AndroidImportance.MAX,
        vibrationPattern: [0, 250, 250, 250],
        lightColor: '#FF231F7C',
      })
    }

    return token
  }

  return (
    <>
      <Header
        backgroundImage={images.otherImages.royaltyBackground}
        backgroundImageStyle={{ borderRadius: 30 }}
        statusBarProps={{ barStyle: 'light-content' }}
        leftContainerStyle={{
          marginLeft: 10,
        }}
        backgroundColor="#F6F7FA"
        containerStyle={{
          height: 150,
        }}
      />
      <GiftedChat
        messages={messages}
        onSend={(messages) => onSend(messages)}
        user={{
          _id: 'alfri.lipari@gmail.com', //put the email of the user maybe
        }}
      />
      <StatusBar style="light" />
    </>
  )
}

export default ChatScreen
